import React from "react";
import {allFilters, FilterType, SearchFilter as SearchFilterType} from "../types/searchFilters.type";
import {UpdateFilters} from "../../searchPage/types/methods.type";
import { SearchFilter } from "./searchFilter.component";

const changeFilter = (i:number, type:string|null, value:string|null, searchFilters:SearchFilterType[], updateFilters:UpdateFilters)=>{
    if (!type||!allFilters.includes(type)) throw new Error(`Cannot change filter type`);
    searchFilters[i] = {filterType:type as FilterType,filterValue: value}
    updateFilters(searchFilters);
}

export function SearchFilters({searchFilters, updateFilters}:{
    searchFilters:SearchFilterType[],
    updateFilters:UpdateFilters
}) {
    return <>{searchFilters.map((f:SearchFilterType, i:number)=><SearchFilter
        searchFilter={f}
        changeFilter={(type:string|null,value:string|null)=>{
            changeFilter(i,type,value,searchFilters,updateFilters)
        }}
    />)}</>;
}
