import React from "react";
import {allFilters, FilterType, SearchFilter as SearchFilterType} from "../types/searchFilters.type";
import {UpdateFilters} from "../../searchPage/types/methods.type";
import { SearchFilter } from "./searchFilter.component";
import {Button} from "@mui/material";

const changeFilter = (i:number, type:string|null, value:string|null, searchFilters:SearchFilterType[], updateFilters:UpdateFilters)=>{
    if (!type||!allFilters.includes(type)) throw new Error(`Cannot change filter type`);
    searchFilters[i] = {filterType:type as FilterType,value: value}
    updateFilters(searchFilters);
}

const addFilter = (searchFilters:SearchFilterType[], updateFilters:UpdateFilters)=>{
    searchFilters.push({filterType:null, value:null});
    updateFilters(searchFilters);
}

const deleteFilter = (index:number, searchFilters:SearchFilterType[], updateFilters:UpdateFilters)=>{
    updateFilters(searchFilters.filter((f,i)=>i!==index))
}

export function SearchFilters({searchFilters, updateFilters}:{
    searchFilters:SearchFilterType[],
    updateFilters:UpdateFilters
}) {
    return <>
        {searchFilters.map((f:SearchFilterType, i:number)=><SearchFilter
            searchFilter={f}
            changeFilter={(type:string|null,value:string|null)=>{
                changeFilter(i,type,value,searchFilters,updateFilters)
            }}
            deleteFilter={()=>deleteFilter(i, searchFilters, updateFilters)}
        />)}
        <br/>
        <Button onClick={()=>addFilter(searchFilters,updateFilters)}>Add filter</Button>
        <Button color='info'>{searchFilters.length===0?'Show all':'Search'}</Button>
    </>;
}
