"use strict";

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('an FileInput with initialFocus and onBlur handler is rendered', () => {
  cy.visitStory('FileInput', 'With initialFocus and onBlur');
});
(0, _steps.When)('the FileInput is blurred', () => {
  cy.focused();
  cy.get('[data-test="dhis2-uicore-fileinput"] button').blur();
});
(0, _steps.Then)('the onBlur handler is called', () => {
  cy.window().then(win => {
    cy.get('[data-test="dhis2-uicore-fileinput"] input').should(fileInput => {
      expect(win.onBlur).to.be.calledWith({
        name: 'upload',
        files: fileInput[0].files
      });
    });
  });
});