"use strict";

var _steps = require("cypress-cucumber-preprocessor/steps");

(0, _steps.Given)('a FileInput with onFocus handler is rendered', () => {
  cy.visitStory('FileInput', 'With onFocus');
});
(0, _steps.When)('the FileInput is focused', () => {
  cy.get('[data-test="dhis2-uicore-fileinput"] button').focus();
});
(0, _steps.Then)('the onFocus handler is called', () => {
  cy.window().then(win => {
    cy.get('[data-test="dhis2-uicore-fileinput"] input').should(fileInput => {
      expect(win.onFocus).to.be.calledWith({
        name: 'upload',
        files: fileInput[0].files
      });
    });
  });
});